import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Header = () => {
  const [language, setLanguage] = useState('EN'); // Default language is English

  const toggleLanguage = () => {
    setLanguage((prevLanguage) => (prevLanguage === 'EN' ? 'SI' : 'EN'));
  };

  return (
    <header>
      <div className="container">
        <nav>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/about-us">About Us</Link></li>
            <li><Link to="/our-ministries">Our Ministries</Link></li>
            <li><Link to="/upcoming-courses">Upcoming Courses</Link></li>
            <li><Link to="/get-involved">Get Involved</Link></li>
            <li><Link to="/prayer-points">Prayer Points</Link></li>
            <li><Link to="/donate">Donate</Link></li>
            <li><Link to="/events">Events</Link></li>
            <li><Link to="/blog-news">Blog/News</Link></li>
            <li><Link to="/contact-us">Contact Us</Link></li>
          </ul>
          <button className="language-toggle" onClick={toggleLanguage}>
            {language === 'EN' ? 'සිංහල' : 'English'}
          </button>
        </nav>
      </div>
    </header>
  );
}

export default Header;
