import React, { useContext } from 'react';
import VideoBanner from '../components/VideoBanner';
import MissionVision from '../components/MissionVision';
import Introduction from '../components/Introduction';

const HomePage = () => {
  const [language, setLanguage] = useState('EN'); // Default language is English

  return (
    <div className="main-content">
      <VideoBanner />
      <h1>{language === 'EN' ? 'Our Mission and Vision' : 'අපේ මෙහෙවර සහ දේශන'}</h1>
      <p>{language === 'EN' ? 'Brief overview of YWAM Kandy’s mission and vision.' : 'YWAM කෑන්ඩියේ මෙහෙවර සහ දේශන පිළිබඳ සාරාංශය.'}</p>
      <h1>{language === 'EN' ? 'Welcome to YWAM Kandy' : 'YWAM කෑන්ඩියට සාදරයෙන් පිළිගනිමු'}</h1>
      <p>{language === 'EN' ? 'Invitation to explore the website further.' : 'වෙබ් අඩවිය තවත් සෙවීමට ආරාධනා.'}</p>
    </div>
  );
}

export default HomePage;
